mapper.insert:

SELECT IFNULL(MAX(reply_num), 0) FROM tiny_reply_community

INSERT
	INTO tiny_reply_community
	VALUES (
		#{reply_num} + 1
		, #{map.community_idx }
		, #{map.reply_name }
		, #{map.reply_content }
		, #{reply_num} + 1
		, 0
		, 0
		, now()
	)


