mapper.insert:

	SELECT IFNULL(MAX(goods_idx), 0) FROM goods

INSERT
	INTO goods
	VALUES (
		#{goods_idx} + 1 -- 굿즈 번호
		, #{goods_name}
		, #{goods_price}
		, #{goods_content}
		, now()
		, #{goods_stock}
		, #{goods_detail}
	)

mapper.update:
UPDATE goods
	SET
		goods_name = #{goods_name}
		, goods_price = #{goods_price}
		, goods_content = #{goods_content}
		, goods_detail = #{goods_detail}
		, goods_stock = #{goods_stock}
	WHERE
		goods_idx = #{goods_idx}

mapper.select:
SELECT *
	FROM file f
		WHERE file_div = 'goods';
