mapper.select:
SELECT *
	FROM product
	WHERE member_id = #{sId}
	ORDER BY product_idx DESC;

SELECT *
	FROM sell
	WHERE sell_id = #{sId}
	ORDER BY sell_idx DESC;

SELECT COUNT(*)
	FROM product
	WHERE member_id = #{sId}

SELECT *
	FROM auction
	WHERE member_id = #{sId}
	ORDER BY auction_idx DESC;

SELECT *
	FROM report
	WHERE seller_id = #{sId}
	ORDER BY report_idx DESC;

SELECT member_rank
	FROM member
	WHERE member_id = #{sId}

